import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatTableModule } from '@angular/material/table';
import { MatIconModule } from '@angular/material/icon';

export interface PeriodicElement {
  name: string;
  position: number;
  weight: number;
  symbol: string;
}
export interface Faculty {
  value: string;
  viewValue: string;
}

const ELEMENT_DATA: PeriodicElement[] = [
  {position: 1, name: 'Hydrogen', weight: 1.0079, symbol: 'H'},
  {position: 2, name: 'Helium', weight: 4.0026, symbol: 'He'},
  {position: 3, name: 'Lithium', weight: 6.941, symbol: 'Li'},
  {position: 4, name: 'Beryllium', weight: 9.0122, symbol: 'Be'},
  {position: 5, name: 'Boron', weight: 10.811, symbol: 'B'},
  {position: 6, name: 'Carbon', weight: 12.0107, symbol: 'C'},
  {position: 7, name: 'Nitrogen', weight: 14.0067, symbol: 'N'},
  {position: 8, name: 'Oxygen', weight: 15.9994, symbol: 'O'},
  {position: 9, name: 'Fluorine', weight: 18.9984, symbol: 'F'},
  {position: 10, name: 'Neon', weight: 20.1797, symbol: 'Ne'},
];

@Component({
  selector: 'app-program-manage',
  standalone: true,
  imports: [
    CommonModule,
    MatTableModule,
    MatFormFieldModule,
    MatSelectModule,
    MatInputModule,
    FormsModule,
    MatIconModule,
  ],
  templateUrl: './program-manage.component.html',
  styleUrls: ['./program-manage.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ProgramManageComponent {
  displayedColumns: string[] = ['position', 'name'];
  programSource = ELEMENT_DATA;
  selectedFaculty: string | null = null;

  faculties: Faculty[] = [
    {value: 'fc', viewValue: 'Faculty of Computing'},
    {value: 'fkm', viewValue: 'Faculty of Mechanical Engineering'},
    {value: 'fke', viewValue: 'Faculty of Engineering'},
  ];

  clearFilter(): void {
    this.selectedFaculty = null;
  }
 }
<div class="select-container">
  <mat-form-field class="custom-mat-form-field">
    <mat-label>Select Faculty</mat-label>
    <mat-select [(value)]="selectedFaculty">
      <mat-option *ngFor="let faculty of faculties" [value]="faculty.value">
        {{ faculty.viewValue }}
      </mat-option>
    </mat-select>
  </mat-form-field>

  <button mat-icon-button (click)="clearFilter()" aria-label="Clear selection">
    <mat-icon>close</mat-icon>
  </button>
</div>


<table mat-table [dataSource]="programSource" class="mat-elevation-z8 ">

  <!-- Position Column -->
  <ng-container matColumnDef="position">
    <th mat-header-cell *matHeaderCellDef> General Requirement </th>
    <td mat-cell *matCellDef="let element"> {{element.position}} </td>
  </ng-container>

  <!-- Name Column -->
  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef> Minimum Grade </th>
    <td mat-cell *matCellDef="let element"> {{element.name}} </td>
  </ng-container>


  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>
