import { CommonModule } from '@angular/common';
import { HttpClient } from '@angular/common/http';
import { AfterViewInit, ChangeDetectionStrategy, ChangeDetectorRef, Component,OnInit, ViewChild } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';
import { MatBadgeModule } from '@angular/material/badge';
import { MatButtonModule } from '@angular/material/button';
import { MatChipsModule } from '@angular/material/chips';
import { MatExpansionModule } from '@angular/material/expansion';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatIconModule } from '@angular/material/icon';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatSnackBar, MatSnackBarModule } from '@angular/material/snack-bar';
import { MatSort, MatSortModule } from '@angular/material/sort';
import { MatTableDataSource, MatTableModule } from '@angular/material/table';
export interface Applicants {
  id: number;
  name: string;
  pre_u: string;
  spm_result: string;
  applied_program: string;
}

@Component({
  selector: 'app-view-applicants',
  standalone: true,
  templateUrl: './view-applicants.component.html',
  styleUrls: ['./view-applicants.component.css'],
  imports:[
   CommonModule,
       MatTableModule,
       MatSortModule,
       MatFormFieldModule,
       MatSelectModule,
       MatInputModule,
       FormsModule,
       MatIconModule,
       MatExpansionModule,
       MatButtonModule,
       MatSnackBarModule,
       ReactiveFormsModule,
       MatChipsModule,
       MatBadgeModule

  ],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ViewApplicantsComponent implements OnInit {
  displayedColumns: string[] = ['id', 'name', 'pre_u', 'spm_result', 'applied_program', 'action'];
  dataSource = new MatTableDataSource<Applicants>();
  public isModalOpen = false;
  public modalTitle = '';
  public modalContent: any = null;
  private apiUrl = 'https://localhost:7108/api/Application';
  // For the add modal:
  public isAddModalOpen = false;
  public addForm: FormGroup;

  // Subject option lists
  spmSubjectOptions = [
    'Bahasa Melayu',
    'Bahasa Cina',
    'Bahasa Ingerris',
    'Sejarah',
    'Pendidikan Moral',
    'Physics',
    'Chemistry',
    'Biology',
    'Mathematics',
    'Additional Mathematics'
  ];
  stpmSubjectOptions = [
    'Mathematics (M)',
    'Mathematics (T)',
    'Physics',
    'Chemistry',
    'Biology',
    'Pengajian Am',
    'MUET'
  ];
  matriculationSubjectOptions = [
    'Physics',
    'Chemistry',
    'Biology',
    'MUET'
  ];
  foundationSubjectOptions = [
    'MUET',
    'Physics',
    'Chemistry',
    'Biology'
  ];
  diplomaSubjectOptions = [
    'MUET'
  ];
  constructor( private fb: FormBuilder,
    private http: HttpClient,
    private cdr: ChangeDetectorRef) {
      this.addForm = this.fb.group({
        name: ['', Validators.required],
        preUType: ['', Validators.required],
        appliedProgram: ['', Validators.required],
        icNumber: ['', Validators.required],
        address: [''],
        gender: [''],
        spmResults: this.fb.array([], Validators.required),
        preUResults: this.fb.array([])
      });
    }

  ngOnInit(): void {
    this.fetchApplicants();
  }

  fetchApplicants(): void {
    this.http.get<Applicants[]>(this.apiUrl).subscribe({
      next: (data: Applicants[]) => {
        this.dataSource.data = data;
        this.cdr.markForCheck();
      },
      error: (err) => console.error('Error fetching data:', err)
    });
  }

  openPreUResultModal(application: Applicants): void {
    this.http.get<any>(`${this.apiUrl}/${application.id}`).subscribe({
      next: (data) => {
        this.modalTitle = data.name +"'s "+ data.preUType +" Result";
        try {
          this.modalContent = JSON.parse(data.preUResult);
        } catch (e) {
          console.error('Error parsing preUResult JSON', e);
          this.modalContent = data.preUResult;
        }
        this.isModalOpen = true;
        this.cdr.markForCheck();
      },
      error: (err) => console.error('Error fetching application details:', err)
    });
  }

  openSpmResultModal(application: Applicants): void {
    this.http.get<any>(`${this.apiUrl}/${application.id}`).subscribe({
      next: (data) => {
        this.modalTitle =data.name +"'s SPM Result";
        try {
          this.modalContent = JSON.parse(data.spmResult);
        } catch (e) {
          console.error('Error parsing spmResult JSON', e);
          this.modalContent = data.spmResult;
        }
        this.isModalOpen = true;
        this.cdr.markForCheck();
      },
      error: (err) => console.error('Error fetching application details:', err)
    });
  }

  openApplicantDetailModal(application: Applicants): void {
    this.http.get<any>(`${this.apiUrl}/detail/${application.id}`).subscribe({
      next: (data) => {
        try {
          data.spmResultParsed = JSON.parse(data.spmResult);
        } catch (e) {
          console.error('Error parsing spmResult JSON', e);
          data.spmResultParsed = data.spmResult;
        }
        try {
          data.preUResultParsed = JSON.parse(data.preUResult);
        } catch (e) {
          console.error('Error parsing preUResult JSON', e);
          data.preUResultParsed = data.preUResult;
        }
        this.modalTitle = 'Applicant Details';
        this.modalContent = data;
        this.isModalOpen = true;
        this.cdr.markForCheck();
      },
      error: (err) => console.error('Error fetching detailed application info:', err)
    });
  }

  closeModal(): void {
    this.isModalOpen = false;
  }

  // Getters for form arrays
  get spmResults(): FormArray {
    return this.addForm.get('spmResults') as FormArray;
  }

  get preUResults(): FormArray {
    return this.addForm.get('preUResults') as FormArray;
  }

  // Methods to add a new result row
  addSpmResult(): void {
    this.spmResults.push(this.fb.group({
      subject: ['', Validators.required],
      grade: ['', Validators.required]
    }));
  }

  addPreUResult(): void {
    this.preUResults.push(this.fb.group({
      subject: ['', Validators.required],
      grade: ['', Validators.required]
    }));
  }

  // Methods to remove a row
  removeSpmResult(index: number): void {
    this.spmResults.removeAt(index);
  }
  removePreUResult(index: number): void {
    this.preUResults.removeAt(index);
  }

  // Filtering available subjects for SPM: remove already selected subjects
  getAvailableSpmSubjects(index: number): string[] {
    const selectedSubjects = this.spmResults.controls
      .filter((ctrl, i) => i !== index)
      .map(ctrl => ctrl.get('subject')?.value)
      .filter(value => !!value);
    return this.spmSubjectOptions.filter(option => !selectedSubjects.includes(option));
  }

  // Filtering available subjects for Pre-U based on the selected preUType in the form
  getAvailablePreUSubjects(index: number): string[] {
    let options: string[] = [];
    const preUType = this.addForm.get('preUType')?.value;
    switch (preUType) {
      case 'STPM':
        options = this.stpmSubjectOptions;
        break;
      case 'Matriculation':
        options = this.matriculationSubjectOptions;
        break;
      case 'Foundation':
        options = this.foundationSubjectOptions;
        break;
      case 'Diploma':
        options = this.diplomaSubjectOptions;
        break;
      default:
        options = [];
    }
    const selectedSubjects = this.preUResults.controls
      .filter((ctrl, i) => i !== index)
      .map(ctrl => ctrl.get('subject')?.value)
      .filter(value => !!value);
    return options.filter(option => !selectedSubjects.includes(option));
  }

  // Method to submit the form
  onAddSubmit(): void {
    if (this.addForm.valid) {
      // Prepare the payload
      const newRecord = this.addForm.value;
      // Convert the results arrays to JSON strings
      newRecord.spmResult = JSON.stringify(newRecord.spmResults);
      newRecord.preUResult = JSON.stringify(newRecord.preUResults);
      // Remove the raw arrays if not needed by the backend:
      delete newRecord.spmResults;
      delete newRecord.preUResults;
      console.log('Submitting new record:', newRecord);
      // Use HTTP POST to submit the new applicant/application record
      this.http.post(this.apiUrl, newRecord).subscribe({
        next: (res) => {
          this.fetchApplicants();
          this.closeAddModal();
        },
        error: (err) => console.error('Error creating record:', err)
      });
    } else {
      console.log('Form is invalid');
    }
  }

  openAddModal(): void {
    // Clear previous data
    this.addForm.reset();
    // Clear form arrays
    while (this.spmResults.length) { this.spmResults.removeAt(0); }
    while (this.preUResults.length) { this.preUResults.removeAt(0); }
    // Optionally add one default row (for SPM is required)
    this.addSpmResult();
    // And one for preU (if you want at least one row)
    this.addPreUResult();
    this.isAddModalOpen = true;
  }

  closeAddModal(): void {
    this.isAddModalOpen = false;
  }

  deleteApplicant(id: number): void {
    this.http.delete(`${this.apiUrl}/${id}`).subscribe({
      next: () => {
        this.fetchApplicants();
      },
      error: (err) => console.error('Error deleting record:', err)
    });
  }

  createApplicant(newApplicant: Applicants): void {
    this.http.post<Applicants>(this.apiUrl, newApplicant).subscribe({
      next: (createdRecord) => {
        this.fetchApplicants();
      },
      error: (err) => console.error('Error creating record:', err)
    });
  }

  updateApplicant(updatedApplicant: Applicants): void {
    this.http.put(`${this.apiUrl}/${updatedApplicant.id}`, updatedApplicant).subscribe({
      next: () => {
        this.fetchApplicants();
      },
      error: (err) => console.error('Error updating record:', err)
    });
  }

}
<div class="filter-container">
  <div class="info-buttons-container">
    <button mat-fab extended class="info-button">
      STPM<br />
      23
    </button>
    <mat-icon class="operator-icon">add</mat-icon>
    <button mat-fab extended class="info-button">Matriculation <br />14</button>
    <mat-icon class="operator-icon">add</mat-icon>
    <button mat-fab extended class="info-button">Foundation <br />13</button>
    <mat-icon class="operator-icon">add</mat-icon>
    <button mat-fab extended class="info-button">Diploma<br />0</button>
    <mat-icon class="operator-icon">drag_handle</mat-icon>
    <button mat-fab extended class="info-button">
      TOTAL <br />
      50 Applicant(s)
    </button>
  </div>
  <br />
  <button mat-fab extended class="scan-button">
    Scan
    <mat-icon>printer</mat-icon>
  </button>

  <button mat-fab extended class="add-button" (click)="openAddModal()">
    Add
    <mat-icon>add</mat-icon>
  </button>
</div>

<table mat-table [dataSource]="dataSource" class="mat-elevation-z8">
  <ng-container matColumnDef="id">
    <th mat-header-cell *matHeaderCellDef>No.</th>
    <td mat-cell *matCellDef="let applicant">{{ applicant.id }}</td>
  </ng-container>

  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef>Name</th>
    <td mat-cell *matCellDef="let applicant">{{ applicant.name }}</td>
  </ng-container>

  <ng-container matColumnDef="pre_u">
    <th mat-header-cell *matHeaderCellDef>Pre-U</th>
    <td mat-cell *matCellDef="let applicant">
      {{ applicant.preUType }}
      <button
        mat-button
        style="color: green"
        (click)="openPreUResultModal(applicant)"
      >
        <mat-icon>search</mat-icon>
      </button>
    </td>
  </ng-container>

  <ng-container matColumnDef="spm_result">
    <th mat-header-cell *matHeaderCellDef>SPM Result</th>
    <td mat-cell *matCellDef="let applicant">
      <!-- {{ applicant.spmResult }} -->
      <button
        mat-button
        style="color: green"
        (click)="openSpmResultModal(applicant)"
      >
        <mat-icon>search</mat-icon>
      </button>
    </td>
  </ng-container>

  <ng-container matColumnDef="applied_program">
    <th mat-header-cell *matHeaderCellDef>Applied Program</th>
    <td mat-cell *matCellDef="let applicant">
      {{ applicant.appliedProgram }}
    </td>
  </ng-container>

  <ng-container matColumnDef="action">
    <th mat-header-cell *matHeaderCellDef>Action</th>
    <td mat-cell *matCellDef="let applicant; let rowIndex = index">
      <button mat-button (click)="openApplicantDetailModal(applicant)">
        <mat-icon style="color: green">search</mat-icon>
      </button>
      <button mat-button style="color: red">
        <mat-icon>delete</mat-icon>
      </button>
    </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
</table>

<div class="modal-overlay" *ngIf="isModalOpen" (click)="closeModal()">
  <div class="modal-content" (click)="$event.stopPropagation()">
    <h2>{{ modalTitle }}</h2>
    <h3>Personal Info</h3>
    <div *ngFor="let app of modalContent.applicants" class="applicant-info">
      <table class="result-table borderless">
        <tbody>
          <tr>
            <td><strong>Name</strong></td>
            <td>: {{ app.name }}</td>
          </tr>
          <tr>
            <td><strong>Pre-U Type</strong></td>
            <td>: {{ modalContent.preUType }}</td>
          </tr>
          <tr>
            <td><strong>Applied Program</strong></td>
            <td>: {{ modalContent.appliedProgram }}</td>
          </tr>
          <tr>
            <td><strong>IC Number</strong></td>
            <td>: {{ app.icNumber }}</td>
          </tr>
          <tr>
            <td><strong>Address</strong></td>
            <td>: {{ app.address }}</td>
          </tr>
          <tr>
            <td><strong>Gender</strong></td>
            <td>: {{ app.gender }}</td>
          </tr>
        </tbody>
      </table>
      <br />
    </div>
    <!-- Application Info: Two separate tables for SPM and Pre-U Results -->
    <div *ngIf="modalTitle === 'Applicant Details' && modalContent">
      <h3>Application Info</h3>

      <h5>SPM Result</h5>
      <table class="result-table">
        <thead>
          <tr>
            <th>Subject</th>
            <th>Grade</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let item of modalContent.spmResultParsed | keyvalue">
            <td>{{ item.key }}</td>
            <td>{{ item.value }}</td>
          </tr>
        </tbody>
      </table>

      <h5>{{ modalContent.preUType }} Result</h5>
      <table class="result-table">
        <thead>
          <tr>
            <th>Subject</th>
            <th>Grade</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let item of modalContent.preUResultParsed | keyvalue">
            <td>{{ item.key }}</td>
            <td>{{ item.value }}</td>
          </tr>
        </tbody>
      </table>
    </div>

    <div *ngIf="modalTitle !== 'Applicant Details' && modalContent">
      <table class="result-table">
        <thead>
          <tr>
            <th>Subject</th>
            <th>Grade</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let item of modalContent | keyvalue">
            <td>{{ item.key }}</td>
            <td>{{ item.value }}</td>
          </tr>
        </tbody>
      </table>
    </div>

    <button
      mat-raised-button
      class="close-button"
      type="button"
      color="primary"
      (click)="closeModal()"
    >
      Cancel
    </button>
  </div>
</div>

<!-- Add Modal -->
<div class="modal-overlay" *ngIf="isAddModalOpen" (click)="closeAddModal()">
  <div class="modal-content" (click)="$event.stopPropagation()">
    <h2>Add New Applicant</h2>
    <form [formGroup]="addForm" (ngSubmit)="onAddSubmit()">
      <!-- Personal Info -->

      <mat-form-field appearance="fill">
        <mat-label>Name:</mat-label>
        <input matInput formControlName="name" placeholder="Enter Name" />
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Pre-U Type</mat-label>
        <mat-select formControlName="preUType">
          <mat-option value="STPM">STPM</mat-option>
          <mat-option value="Matriculation">Matriculation</mat-option>
          <mat-option value="Foundation">Foundation</mat-option>
          <mat-option value="Diploma">Diploma</mat-option>
        </mat-select>
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Applied Program:</mat-label>
        <input
          matInput
          formControlName="appliedProgram"
          placeholder="Enter Applied Program"
        />
      </mat-form-field>
      <mat-form-field appearance="fill">
        <mat-label>IC Number:</mat-label>
        <input
          matInput
          formControlName="icNumber"
          placeholder="Enter IC Number"
        />
      </mat-form-field>
      <mat-form-field appearance="fill">
        <mat-label>Address:</mat-label>
        <input matInput formControlName="address" placeholder="Enter Address" />
      </mat-form-field>
      <mat-form-field appearance="fill">
        <mat-label>Gender:</mat-label>
        <input matInput formControlName="gender" placeholder="Enter Gender" />
      </mat-form-field>
      <br />

      <!-- SPM Results Section (Required) -->
      <h3>SPM Results</h3>
      <div formArrayName="spmResults">
        <div
          *ngFor="let spmGroup of spmResults.controls; let i = index"
          [formGroupName]="i"
        >
          <mat-form-field appearance="fill">
            <mat-label>Subject</mat-label>
            <mat-select formControlName="subject">
              <mat-option
                *ngFor="let subject of getAvailableSpmSubjects(i)"
                [value]="subject"
              >
                {{ subject }}
              </mat-option>
            </mat-select>
          </mat-form-field>
          <mat-form-field appearance="fill">
            <mat-label>Grade</mat-label>
            <input matInput formControlName="grade" placeholder="Enter Grade" />
          </mat-form-field>
          <button mat-button type="button" (click)="removeSpmResult(i)">
            Remove
          </button>
        </div>
      </div>
      <button mat-raised-button type="button" (click)="addSpmResult()">
        Add SPM Result
      </button>

      <br /><br />

      <!-- Pre-U Results Section (Choose One) -->
      <h3>Pre-U Results</h3>
      <div formArrayName="preUResults">
        <div
          *ngFor="let preUGroup of preUResults.controls; let j = index"
          [formGroupName]="j"
        >
          <mat-form-field class="subject" appearance="fill">
            <mat-label>Subject</mat-label>
            <mat-select formControlName="subject">
              <mat-option
                *ngFor="let subject of getAvailablePreUSubjects(j)"
                [value]="subject"
              >
                {{ subject }}
              </mat-option>
            </mat-select>
          </mat-form-field>
          <mat-form-field appearance="fill">
            <mat-label>Grade</mat-label>
            <input matInput formControlName="grade" placeholder="Enter Grade" />
          </mat-form-field>
          <button mat-button type="button" (click)="removePreUResult(j)">
            Remove
          </button>
        </div>
      </div>
      <button mat-raised-button type="button" (click)="addPreUResult()">
        Add Pre-U Result
      </button>

      <br /><br />
      <button mat-raised-button color="primary" type="submit">Submit</button>
      <button
        mat-raised-button
        color="warn"
        type="button"
        (click)="closeAddModal()"
      >
        Cancel
      </button>
    </form>
  </div>
</div>
