import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatIconModule } from '@angular/material/icon';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatTableModule } from '@angular/material/table';
import {MatExpansionModule} from '@angular/material/expansion';
export interface GeneralEntryRequirement {
  category: string;
  id: number;
  requirement: string;
  grade: string;
}
export interface Faculty {
  value: string;
  viewValue: string;
}

const GENERAL_DATA: GeneralEntryRequirement[] = [
  {id: 1, category: 'SPM', requirement: 'Malay', grade: 'C'},
  {id: 2, category: 'SPM', requirement: 'English', grade: 'C'},
  {id: 3, category: 'STPM', requirement: 'Pengajian Am', grade: 'C'},
  {id: 4, category: 'Matriculation', requirement: 'PNGK', grade: '2.00'},
  {id: 5, category: 'Diploma', requirement: 'PNGK', grade: '2.00'},
  {id: 6, category: 'MUET', requirement: 'Band', grade: '1.00'},
];

@Component({
  selector: 'app-program-manage',
  standalone: true,
  imports: [
    CommonModule,
    MatTableModule,
    MatFormFieldModule,
    MatSelectModule,
    MatInputModule,
    FormsModule,
    MatIconModule,
    MatExpansionModule
  ],
  templateUrl: './program-manage.component.html',
  styleUrls: ['./program-manage.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ProgramManageComponent {
  displayedColumns: string[] = ['category','requirement', 'grade'];
  programSource = GENERAL_DATA;
  selectedFaculty: string | null = null;

  faculties: Faculty[] = [
    {value: 'fc', viewValue: 'Faculty of Computing'},
    {value: 'fkm', viewValue: 'Faculty of Mechanical Engineering'},
    {value: 'fke', viewValue: 'Faculty of Engineering'},
  ];

  clearFilter(): void {
    this.selectedFaculty = null;
  }
 }
<div class="filter-container">
  <mat-form-field class="custom-mat-form-field" appearance="fill">
    <mat-label>Select Faculty</mat-label>
    <mat-select [(value)]="selectedFaculty">
      <mat-option *ngFor="let faculty of faculties" [value]="faculty.value">
        {{ faculty.viewValue }}
      </mat-option>
    </mat-select>
  </mat-form-field>
  <button mat-icon-button class="clear-button" *ngIf="selectedFaculty" (click)="clearFilter()">
    <mat-icon>close</mat-icon>
  </button>
</div>

<mat-accordion *ngIf="selectedFaculty">
  <mat-expansion-panel hideToggle>
    <mat-expansion-panel-header>
      <mat-panel-title> This is the expansion title </mat-panel-title>
      <mat-panel-description> This is a summary of the content </mat-panel-description>
    </mat-expansion-panel-header>
    <p>This is the primary content of the panel.</p>
  </mat-expansion-panel>
  <mat-expansion-panel (opened)="panelOpenState.set(true)" (closed)="panelOpenState.set(false)">
    <mat-expansion-panel-header>
      <mat-panel-title> Self aware panel </mat-panel-title>
      <mat-panel-description>
        Currently I am {{panelOpenState() ? 'open' : 'closed'}}
      </mat-panel-description>
    </mat-expansion-panel-header>
    <p>I'm visible because I am open</p>
  </mat-expansion-panel>
</mat-accordion>

<table mat-table [dataSource]="programSource" class="mat-elevation-z8 ">

  <ng-container matColumnDef="category">
    <th mat-header-cell *matHeaderCellDef> General Requirement </th>
    <td mat-cell *matCellDef="let element"> {{element.category}} </td>
  </ng-container>

  <ng-container matColumnDef="requirement">
    <th mat-header-cell *matHeaderCellDef> Minimum Grade </th>
    <td mat-cell *matCellDef="let element"> {{element.requirement}} </td>
  </ng-container>

  <ng-container matColumnDef="grade">
    <th mat-header-cell *matHeaderCellDef> Minimum Grade </th>
    <td mat-cell *matCellDef="let element"> {{element.grade}} </td>
  </ng-container>


  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>
