      fc: [{
        code: 'UT6481001',
        name: 'Software Engineering'
      }, {
        code: 'UT6481002',
        name: 'Data Engineering'
      }, {
        code: 'UT6481003',
        name: 'Bioinformatics'
      }, {
        code: 'UT6481004',
        name: 'Network and Cybersecurity'
      }, {
        code: 'UT6481005',
        name: 'Graphics and Multimedia'
      }],
      fkm: [{
        code: 'UT6521001',
