import { CommonModule } from '@angular/common';
import { HttpClient } from '@angular/common/http';
import { AfterViewInit, ChangeDetectionStrategy, ChangeDetectorRef, Component, ViewChild } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatExpansionModule } from '@angular/material/expansion';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatIconModule } from '@angular/material/icon';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatSnackBar, MatSnackBarModule } from '@angular/material/snack-bar';
import { MatSort, MatSortModule } from '@angular/material/sort';
import { MatTableDataSource, MatTableModule } from '@angular/material/table';

export interface Faculty {
  value: string;
  viewValue: string;
}

export interface Program {
  code: string;
  name: string;
}

export interface SpecialEntryRequirement {
  id: number;
  category: string;
  requirement: string;
  grade: string;
}


@Component({
  selector: 'app-program-manage',
  standalone: true,
  imports: [
    CommonModule,
    MatTableModule,
    MatSortModule,
    MatFormFieldModule,
    MatSelectModule,
    MatInputModule,
    FormsModule,
    MatIconModule,
    MatExpansionModule,
    MatButtonModule,
    MatSnackBarModule
  ],
  templateUrl: './program-manage.component.html',
  styleUrls: ['./program-manage.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ProgramManageComponent implements AfterViewInit {
  constructor(private http: HttpClient, private cdr: ChangeDetectorRef, private snackBar: MatSnackBar
  ) {
    this.getGeneralRequirement();
    this.createForm = this.fb.group({
      graduate_type: ['', Validators.required],
      requirement_type: ['', Validators.required],
      grade: ['', Validators.required],
      type: ['', Validators.required],
      faculty: [''],
      program_code: [''],
    });
  }
  createForm: FormGroup;
  isSpecialSelected: boolean = false;
  displayedColumns: string[] = ['graduate_type', 'requirement', 'grade', 'action'];
  generalRequirement: MatTableDataSource<any> = new MatTableDataSource<any>([]);
  selectedFaculty: string | null = null;
  selectedProgramCode: string | null = null;
  specialRequirements: { [programCode: string]: SpecialEntryRequirement[] } = {};
  editingRowIndex: number | null = null;
  updatedGrade: string | null = null;
  editingSpecialRowIndex: number | null = null;
  updatedSpecialGrade: string | null = null;
  isCreateModalOpen: boolean = false;


  faculties: Faculty[] = [
    { value: 'fc', viewValue: 'Faculty of Computing' },
    { value: 'fkm', viewValue: 'Faculty of Mechanical Engineering' },
    { value: 'fke', viewValue: 'Faculty of Engineering' },
  ];

  getGeneralRequirement() {
    this.http.get('https://localhost:7108/api/EntryRequirement/general')
      .subscribe((res: any) => {
        this.generalRequirement = new MatTableDataSource(res);
        if (this.sort) {
          this.generalRequirement.sort = this.sort;
        }
        this.cdr.markForCheck();
      });
  }

  @ViewChild(MatSort, { static: false }) sort: MatSort | undefined;
  ngAfterViewInit() {
    if (this.sort) {
      this.generalRequirement.sort = this.sort;
    }
  }


  getSpecialRequirements(programCode: string) {
    this.http.get<SpecialEntryRequirement[]>(`https://localhost:7108/api/EntryRequirement/special/${programCode}`)
      .subscribe((data) => {
        this.specialRequirements[programCode] = data;
        this.cdr.markForCheck();
      });
  }

  startEditing(rowIndex: number, currentGrade: string) {
    this.editingRowIndex = rowIndex;
    this.updatedGrade = currentGrade;
  }

  saveChanges(rowIndex: number) {
    const row = this.generalRequirement.data[rowIndex];
    const updatedRequirement = {
      id: row.id,
      grade: this.updatedGrade,
      subject: row.subject,
      requirement_type: row.requirement_type,
      graduate_type: row.graduate_type,
    };

    this.http.put(`https://localhost:7108/api/EntryRequirement/update/${row.id}`, updatedRequirement).subscribe(
      (res) => {
        console.log('Update successful:', res);
        this.snackBar.open('Grade edited successfully!', 'Close', {
          duration: 2000,
          horizontalPosition: 'right',
          verticalPosition: 'top',
        });
        this.generalRequirement.data[rowIndex].grade = this.updatedGrade;
        this.editingRowIndex = null;
        this.updatedGrade = null;
        console.log(this.editingRowIndex, this.updatedGrade);
        this.cdr.markForCheck();
      },
      (err) => {
        console.error('Update failed:', err);
      }
    );
  }

  startEditingSpecial(programCode: string, rowIndex: number, currentGrade: string) {
    this.editingSpecialRowIndex = rowIndex;
    this.updatedSpecialGrade = currentGrade;
  }

  saveSpecialChanges(programCode: string, rowIndex: number) {
    const specialRequirement = this.specialRequirements[programCode][rowIndex];
    const updatedRequirement = {
      ...specialRequirement,
      grade: this.updatedSpecialGrade,
    };

    this.http.put(`https://localhost:7108/api/EntryRequirement/update/${specialRequirement.id}`, updatedRequirement).subscribe(
      (res) => {
        console.log('Special Requirement Update Successful:', res);
        this.snackBar.open('Grade edited successfully!', 'Close', {
          duration: 2000,
          horizontalPosition: 'right',
          verticalPosition: 'top',
        });
        if (this.updatedSpecialGrade !== null) {
          this.specialRequirements[programCode][rowIndex].grade = this.updatedSpecialGrade;
        }
        this.editingSpecialRowIndex = null;
        this.updatedSpecialGrade = null;
        this.cdr.markForCheck();
      },
      (err) => {
        console.error('Update failed:', err);
      }
    );
  }

  confirmDelete(id: number,programCode?: string) {
    const confirmResult = window.confirm("Are you sure you want to delete this entry?");
    if (confirmResult) {
      this.deleteEntryRequirement(id,programCode);
    }
  }

  deleteEntryRequirement(id: number ,programCode?: string) {
    this.http.delete(`https://localhost:7108/api/EntryRequirement/delete/${id}`, { responseType: 'text' })
      .subscribe(
        (response) => {
          console.log('Delete successful:', response);
          this.generalRequirement.data = this.generalRequirement.data.filter(item => item.id !== id);
          if (programCode) {
            this.specialRequirements[programCode] = this.specialRequirements[programCode].filter(item => item.id !== id);
          }

          this.snackBar.open('Entry requirement deleted successfully!', 'Close', {
            duration: 2000,
            horizontalPosition: 'right',
            verticalPosition: 'top',
          });

          this.cdr.detectChanges();
        },
        (error) => {
          console.error('Delete failed:', error);
          this.snackBar.open('Failed to delete entry requirement.', 'Close', {
            duration: 2000,
            horizontalPosition: 'right',
            verticalPosition: 'top',
          });
        }
      );
  }


  clearFilter(): void {
    this.selectedFaculty = null;
    this.selectedProgramCode = null;
  }

  getProgramsForFaculty(): Program[] {
    return this.selectedFaculty ? this.programs[this.selectedFaculty] : [];
  }

  selectProgram(programCode: string): void {
    this.selectedProgramCode = programCode;
    this.getSpecialRequirements(programCode);
  }

  programs: { [key: string]: Program[] } = {
    fc: [
      { code: 'UT6481001', name: 'Software Engineering' },
      { code: 'UT6481002', name: 'Data Engineering' },
      { code: 'UT6481003', name: 'Bioinformatics' },
      { code: 'UT6481004', name: 'Network and Cybersecurity' },
      { code: 'UT6481005', name: 'Graphics and Multimedia' },
    ],
    fkm: [
      { code: 'UT6521001', name: 'Pure Mechanics' },
      { code: 'UT6521003', name: 'Manufacturing' },
      { code: 'UT6521004', name: 'Industrial' },
      { code: 'UT6525001', name: 'Aerospace' },
      { code: 'UT6525002', name: 'Automotive' },
      { code: 'UT6525003', name: 'Offshore' },
    ],
    fke: [
      { code: 'UT6522002', name: 'Electrical Engineering' },
      { code: 'UT6523001', name: 'Electronic Engineering' },
      { code: 'UT6523002', name: 'Mechatronics Engineering' },
      { code: 'UT6523003', name: 'Biomedical Engineering' },
    ],
  };

}
<div class="filter-container">
  <mat-form-field class="custom-mat-form-field" appearance="fill">
    <mat-label>Select Faculty</mat-label>
    <mat-select [(value)]="selectedFaculty">
      <mat-option *ngFor="let faculty of faculties" [value]="faculty.value">
        {{ faculty.viewValue }}
      </mat-option>
    </mat-select>
  </mat-form-field>
  <button mat-icon-button class="clear-button" *ngIf="selectedFaculty" (click)="clearFilter()">
    <mat-icon>close</mat-icon>
  </button>

  <button mat-fab extended class="add-button"> Add
    <mat-icon>add</mat-icon>
  </button>
</div>



<mat-accordion *ngIf="selectedFaculty">
  <mat-expansion-panel disabled>
    <mat-expansion-panel-header style="color: #9d9d9d;">
      💡 Click to expand and view specific entry requirement
    </mat-expansion-panel-header>
  </mat-expansion-panel>
  <mat-expansion-panel *ngFor="let program of getProgramsForFaculty()" hideToggle>
    <mat-expansion-panel-header (click)="selectProgram(program.code)">
      <mat-panel-title> {{ program.code }}  </mat-panel-title>
      <mat-panel-description style="color: black;"> {{ program.name }} </mat-panel-description>
    </mat-expansion-panel-header>
    <table mat-table [dataSource]="specialRequirements[program.code]" class="mat-elevation-z8">
      <ng-container matColumnDef="graduate_type">
        <th mat-header-cell *matHeaderCellDef > Category </th>
        <td mat-cell *matCellDef="let element"> {{ element.graduate_type }} </td>
      </ng-container>

      <ng-container matColumnDef="requirement">
        <th mat-header-cell *matHeaderCellDef> Special Requirement </th>
        <td mat-cell *matCellDef="let element"> {{ element.subject }} </td>
      </ng-container>

      <ng-container matColumnDef="grade">
        <th mat-header-cell *matHeaderCellDef style="width: 30%;"> Minimum Grade </th>
        <td mat-cell *matCellDef="let element; let rowIndex = index">
          <ng-container *ngIf="editingSpecialRowIndex === rowIndex; else gradeView">
            <input matInput [(ngModel)]="updatedSpecialGrade" />
          </ng-container>
          <ng-template #gradeView>{{ element.grade }}</ng-template>
        </td>
      </ng-container>

      <ng-container matColumnDef="action">
        <th mat-header-cell *matHeaderCellDef> Actions </th>
        <td mat-cell *matCellDef="let element; let rowIndex = index">
          <button mat-button *ngIf="editingSpecialRowIndex === rowIndex; else editButton"
                  (click)="saveSpecialChanges(program.code, rowIndex)">
            <mat-icon>check</mat-icon>
          </button>
          <ng-template #editButton>
            <button mat-button (click)="startEditingSpecial(program.code, rowIndex, element.grade)">
              <mat-icon>edit</mat-icon>
            </button>
          </ng-template>
          <button mat-button (click)="confirmDelete(element.id, program.code)">
            <mat-icon >delete</mat-icon>
          </button>
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
    </table>
  </mat-expansion-panel>
</mat-accordion>

<table mat-table matSort [dataSource]="generalRequirement" class="mat-elevation-z8">
  <ng-container matColumnDef="graduate_type">
    <th mat-header-cell *matHeaderCellDef mat-sort-header> Category </th>
    <td mat-cell *matCellDef="let element"> {{ element.graduate_type }} </td>
  </ng-container>

  <ng-container matColumnDef="requirement">
    <th mat-header-cell *matHeaderCellDef> General Requirement </th>
    <td mat-cell *matCellDef="let element"> {{ element.subject }} </td>
  </ng-container>

  <ng-container matColumnDef="grade">
    <th mat-header-cell *matHeaderCellDef style="width: 30%;"> Minimum Grade </th>
    <td mat-cell *matCellDef="let element; let generalRowIndex = index">
      <ng-container *ngIf="editingRowIndex === generalRowIndex; else gradeView">
        <input matInput [(ngModel)]="updatedGrade" />
      </ng-container>
      <ng-template #gradeView>{{ element.grade }}</ng-template>
    </td>
  </ng-container>


  <ng-container matColumnDef="action">
    <th mat-header-cell *matHeaderCellDef> Actions </th>
    <td mat-cell *matCellDef="let element; let rowIndex = index">
      <button mat-button *ngIf="editingRowIndex === rowIndex; else editButton" (click)="saveChanges(rowIndex)">
        <mat-icon>check</mat-icon>
      </button>
      <ng-template #editButton>
        <button mat-button (click)="startEditing(rowIndex, element.grade)">
          <mat-icon>edit</mat-icon>
        </button>
      </ng-template>
      <button mat-button (click)="confirmDelete(element.id)">
        <mat-icon>delete</mat-icon>
      </button>
    </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>

<div *ngIf="isCreateModalOpen" class="modal-overlay">
  <div class="modal-content">
    <h2>Edit User Form</h2>
    <form [formGroup]="createForm">
      <!-- Category Selection -->
      <mat-form-field appearance="fill">
        <mat-label>Category</mat-label>
        <mat-select formControlName="graduate_type">
          <mat-option value="SPM">SPM</mat-option>
          <mat-option value="STPM">STPM</mat-option>
          <mat-option value="MUET">MUET</mat-option>
          <mat-option value="Diploma">Diploma</mat-option>
          <mat-option value="Matriculation">Matriculation</mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Subject (Requirement Type) Selection -->
      <mat-form-field appearance="fill">
        <mat-label>Subject (Requirement Type)</mat-label>
        <mat-select formControlName="requirement_type">
          <mat-option *ngFor="let subject of requirementTypes" [value]="subject">{{ subject }}</mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Minimum Grade Selection -->
      <mat-form-field appearance="fill">
        <mat-label>Minimum Grade</mat-label>
        <mat-select formControlName="grade">
          <mat-option *ngFor="let grade of grades" [value]="grade">{{ grade }}</mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Type Selection (General/Special) -->
      <mat-form-field appearance="fill">
        <mat-label>Type</mat-label>
        <mat-select formControlName="type" (selectionChange)="onTypeChange($event)">
          <mat-option value="general">General</mat-option>
          <mat-option value="special">Special</mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Faculty Selection for Special Type -->
      <mat-form-field *ngIf="isSpecialSelected" appearance="fill">
        <mat-label>Faculty</mat-label>
        <mat-select formControlName="faculty">
          <mat-option value="fc">Faculty of Computing</mat-option>
          <mat-option value="fkm">Faculty of Mechanical Engineering</mat-option>
          <mat-option value="fke">Faculty of Engineering</mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Program Selection for Special Type -->
      <mat-form-field *ngIf="isSpecialSelected && selectedFaculty" appearance="fill">
        <mat-label>Program</mat-label>
        <mat-select formControlName="program_code">
          <mat-option *ngFor="let program of getProgramsForFaculty()" [value]="program.code">
            {{ program.name }}
          </mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Update and Cancel Buttons -->
      <br />
      <div style="float: right;">
        <button mat-raised-button color="primary" type="button" (click)="submitEntry()">Submit</button>
        <button mat-raised-button color="warn" type="button" (click)="closeCreateModal()">Cancel</button>
      </div>
    </form>
  </div>
</div>
