utton[_ngcontent-%COMP%]{\n  margin: 10px;\n}\n\nmat-form-field[_ngcontent-%COMP%] {\n  width: 100%;\n  input{\n    width: 100%;\n  }\n}\n.category-tag[_ngcontent-%COMP%] {\n  display: inline-block;\n  padding: 6px 12px;\n  border-radius: 20px;\n  font-size: 14px;\n  color: white;\n  font-weight: bold;\n}\n\n.spm-tag[_ngcontent-%COMP%] {\n  background-color: #f44336; \n}\n\n.stpm-tag[_ngcontent-%COMP%] {\n  background-color: #2196F3; \n}\n\n.muet-tag[_ngcontent-%COMP%] {\n  background-color: #4CAF50; \n}\n\n.diploma-tag[_ngcontent-%COMP%] {\n  background-color: #FF9800; \n}\n\n.matriculation-tag[_ngcontent-%COMP%] {\n  background-color: #9C27B0; \n}\n.modal-overlay-sub[_ngcontent-%COMP%] {\n  position: fixed;\n  top: 0;\n  left: 0;\n  width: 100%;\n  height: 100%;\n  background: rgba(0, 0, 0, 0.5);\n  display: flex;\n  justify-content: center;\n  align-items: center;\n}\n\n.modal-content-sub[_ngcontent-%COMP%] {\n  background: white;\n  width: 80%;\n  max-width: 600px;\n  height: 80vh; \n  display: flex;\n  flex-direction: column;\n  border-radius: 8px;\n  overflow: hidden; \n}\n\n.modal-body-sub[_ngcontent-%COMP%] {\n  flex: 1;\n  overflow-y: auto; \n  padding: 20px;\n}\n\n.table-container-sub[_ngcontent-%COMP%] {\n  width: 100%;\n  overflow-y: auto; \n  margin-bottom: 20px;\n}\n\n.modal-actions-sub[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: flex-start;\n  gap: 10px;\n}\n\n.subject-button[_ngcontent-%COMP%]{\n  margin-right: 5px;\n  margin-bottom: 10px;\n  float: right;\n}\n\n.full-width-table[_ngcontent-%COMP%] {\n  width: 100%; \n  table-layout: auto; \n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"],
