      </mat-form-field> -->

      <!-- Matriculation Subject Selection (Based on Requirement Type) -->
      <!-- <mat-form-field *ngIf="isMatriculation" appearance="fill">
        <mat-label>Subject</mat-label>
        <mat-select formControlName="subject">
          <mat-option value="Physics">Physics</mat-option>
          <mat-option value="Chemistry">Chemistry</mat-option>
          <mat-option value="Biology">Biology</mat-option>
          <mat-option value="MUET">MUET</mat-option>
        </mat-select>
      </mat-form-field> -->

      <!-- Diploma Subject Selection (Based on Requirement Type) -->
      <!-- <mat-form-field *ngIf="isDiploma" appearance="fill">
        <mat-label>Subject</mat-label>
        <mat-select formControlName="subject">
          <mat-option value="MUET">MUET</mat-option>
        </mat-select>
      </mat-form-field> -->

      <!-- Grade Input -->
      <mat-form-field *ngIf="selectedCategory" appearance="fill">
        <mat-label>Grade</mat-label>
        <mat-select [(ngModel)]="selectedGrade" formControlName="grade">
          <mat-option *ngFor="let grade of grades" [value]="grade">
            {{ grade }}
          </mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Type Selection (General/Special) -->
      <mat-form-field appearance="fill">
        <mat-label>Type</mat-label>
        <mat-select
          formControlName="requirement_type"
          (selectionChange)="onTypeChange($event)"
        >
          <mat-option value="general">General</mat-option>
          <mat-option value="special">Special</mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Faculty Selection for Special Type -->
      <mat-form-field *ngIf="isSpecialSelected" appearance="fill">
        <mat-label>Faculty</mat-label>
        <mat-select formControlName="faculty">
          <mat-option
            *ngFor="let faculty of faculties"
            [value]="faculty.facultyCode"
          >
            {{ faculty.faculty }}
          </mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Program Selection for Special Type -->
      <mat-form-field
        *ngIf="isSpecialSelected && createForm.value.faculty"
        appearance="fill"
      >
        <mat-label>Program</mat-label>
        <mat-select formControlName="program_code">
          <mat-option
            *ngFor="let program of getProgramsForFacultyDropdown()"
            [value]="program.code"
          >
            {{ program.name }}
          </mat-option>
        </mat-select>
      </mat-form-field>

      <!-- Display error message if any -->
      <div
        *ngIf="errorMessage"
        style="color: red; font-size: 14px; margin-top: 10px"
      >
        {{ errorMessage }}
      </div>

      <!-- Update and Cancel Buttons -->
      <br />
      <div style="float: right">
        <button
          mat-raised-button
          color="primary"
          type="button"
          (click)="submitEntry()"
        >
          Submit
        </button>
        <button
          mat-raised-button
          color="warn"
          type="button"
          (click)="closeCreateModal()"
        >
          Cancel
        </button>
      </div>
    </form>
  </div>
</div>
<!-- Subject Modal -->
<div *ngIf="isSubjectModalOpen" class="modal-overlay-sub">
  <div class="modal-content-sub">

    <div class="modal-body-sub">


      <!-- Add Subject Form -->
      <h4>Add Subject</h4>
      <form [formGroup]="subjectForm">
        <mat-form-field>
          <mat-label>Category</mat-label>
          <mat-select formControlName="category">
            <mat-option *ngFor="let cat of categories" [value]="cat">{{ cat }}</mat-option>
          </mat-select>
        </mat-form-field>

        <mat-form-field>
          <mat-label>Subject Name</mat-label>
          <input matInput type="text" formControlName="subjectName" />
        </mat-form-field>

        <div class="modal-actions">
          <button mat-raised-button color="warn" type="button" (click)="closeSubjectModal()" class="subject-button">
            Cancel
          </button>
          <button mat-raised-button color="primary" type="button" (click)="saveSubject()" [disabled]="subjectForm.invalid" class="subject-button">
            Add
          </button>
        </div>
      </form>
      <button mat-raised-button color="accent" (click)="toggleViewSubjects()" class="subject-button">
        <mat-icon>{{ showSubjects ? "visibility_off" : "visibility" }}</mat-icon>
        {{ showSubjects ? "Hide Subjects" : "View All Subjects" }}
      </button>
      <div *ngIf="showSubjects" class="table-container-sub">
        <table mat-table [dataSource]="subjectDataSource" matSort class="full-width-table"  #subjectSort="matSort">

          <!-- Category Column with Sorting -->
          <ng-container matColumnDef="category">
            <th mat-header-cell *matHeaderCellDef mat-sort-header>Category</th>
            <td mat-cell *matCellDef="let subject">
              <span class="category-tag" [ngClass]="getCategoryClass(subject.category)">
                {{ subject.category }}
              </span>
            </td>
          </ng-container>

          <!-- Subject Name Column -->
          <ng-container matColumnDef="subjectName">
            <th mat-header-cell *matHeaderCellDef>Subject Name</th>
            <td mat-cell *matCellDef="let subject">{{ subject.subjectName }}</td>
          </ng-container>

          <!-- Actions Column -->
          <ng-container matColumnDef="actions">
            <th mat-header-cell *matHeaderCellDef>Actions</th>
            <td mat-cell *matCellDef="let subject">
              <button mat-icon-button color="warn" (click)="deleteSubject(subject.id)">
                <mat-icon>delete</mat-icon>
              </button>
            </td>
          </ng-container>

          <!-- Table Headers & Rows -->
          <thead>
            <tr mat-header-row *matHeaderRowDef="displayedCourseColumns"></tr>
          </thead>
          <tbody>
            <tr mat-row *matRowDef="let row; columns: displayedCourseColumns;"></tr>
          </tbody>
        </table>
      </div>

    </div>
  </div>
</div>
