    <td mat-cell *matCellDef="let applicant">
      {{ applicant.preUType }}
      <button mat-button style="color: green;"  (click)="openPreUResultModal(applicant)">
        <mat-icon>search</mat-icon>
      </button>
    </td>
  </ng-container>

